// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;



contract Bank {

uint public totalBalance;
uint interest;
address owner;

mapping(address => uint) public balances;

modifier onlyOwner{
    require(msg.sender == owner);
    _;
}

 event depositSuccessful(string sent, uint amount);

constructor(uint _interest){
    interest = _interest; 
    owner = msg.sender;
}

function re() public view returns(address){
    return address(this);
}

function deposit() external payable returns(bool){
    require(msg.value > 0, "Cannot deposit Zero");
    balances[msg.sender] += msg.value;

    emit depositSuccessful("Sent", msg.value);
    return true;
} 

function withdraw(uint _amount) external  {
    require(balances[msg.sender] >= _amount, "Insufficient funds");
    require(address(this).balance >= _amount, "Contract cannot pay");

    balances[msg.sender]-= _amount;
    (bool success,) = msg.sender.call{value: _amount}(" ");
    require(success, "transcation failed");


}

function drain(address recepient) public onlyOwner{
    uint amount =  address(this).balance;

    (bool success,) = recepient.call{value: amount}(" ");
    require(success, "transcation failed");
}

// receive() external payable {}


}

contract Todo{
    uint256 todoCounter;

    enum Status{
        Pending,
        Done,
        Cancelled,
        Defaulted
    }

    struct TodoList{
        uint id;
        address owner;
        string text;
        Status status;
        uint256 deadline;
    }

    mapping(uint => TodoList) todos;

    event TodoCreated(string text, uint deadline);



function createTodo(string memory _text, uint _deadline) external returns(uint){
    require(bytes(_text).length > 0, "Empty text");
    require(_deadline > (block.timestamp + 600), "Invalid deadline");
    require(msg.sender != address(0), "Zero address");

    todoCounter++;

    todos[todoCounter] = TodoList(todoCounter, msg.sender, _text, Status.Pending, _deadline);

    emit TodoCreated(_text, _deadline);
    return todoCounter;    
}

function markAsDone(uint _id) external {
    require((_id > 0) && (_id <= todoCounter) , 'invalid id');
    TodoList storage todo = todos[_id];
    require(todo.status == Status.Pending, "Not pending");
    require(msg.sender == todo.owner, "unauthorized Caller");

    if(block.timestamp > todo.deadline){
        todo.status = Status.Defaulted;
    }
    else{
    todo.status = Status.Done;
    }

}

}
