// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.28;

contract ERC20{
    string public name;
    string public symbol;
    uint8 public decimals;

    uint private _totalsupply;

    mapping (address => uint) private _balances;
mapping (address => mapping (address => uint)) private  _allowances;

event Transfer(address indexed from, address indexed to,uint value);
event Approval (address indexed owner,address indexed spender, uint value );

constructor( string memory _name, string memory _symbol, uint8 _decimals, uint initialSupply){
    name = _name;
    symbol = _symbol;
    decimals = _decimals;

    // Mint 
_mint(msg.sender, initialSupply);
}


function totalsupply() external view returns (uint) {
    return _totalsupply;

}

function allowance(address owner, address spender) external view returns (uint){
    return _allowances[owner][spender];
}

function _transfer(address from, address to, uint amount) internal {
    require(to != address(0), "transfer to address zero");
uint senderBalance = _balances[from];
require(senderBalance >= amount, "insufficient funds");

unchecked {
_balances[from] = senderBalance  - amount;
_balances[to] += amount;
}


emit Transfer(from, to, amount);
}

function _mint(address account, uint amount) internal {
    require(  account != address(0), "cannot mint to address zero");
    _totalsupply += amount;
    _balances[account] += amount;

    emit Transfer(address(0), account, amount);

}

function _burn(uint amount)internal{
    require(_balances[msg.sender] >= amount, 'insufficient funds');
    require(msg.sender != address(0), "cannot call from address zero");

_totalsupply -= amount;
    _balances[msg.sender] -= amount;
    _totalsupply -= amount;

    emit Transfer(msg.sender, address(0), amount);
}

function approve(address spender, uint amount) external returns (bool) {
    _allowances[msg.sender][spender] = amount;
    emit Approval(msg.sender, spender, amount);
    return true;

}

function transferFrom(address from, address to, uint amount) external returns (bool){ 
uint currentAllowance = _allowances[from][msg.sender];
require(currentAllowance >= amount, "insufficient allowaance");

_allowances[from][msg.sender] = currentAllowance - amount;

emit Approval(from, msg.sender, _allowances[from][msg.sender]);
_transfer(from,to,amount);
return true;
}

 function transfer(address to, uint amount) external returns (bool){
    _transfer(msg.sender, to, amount);
    return true;
 }

 function mint (uint amount)external returns (bool) {
    _mint(msg.sender, amount);
    return true;
 }
}
